    @Test
    public void test_regex1(){
        String regex = "^(.{0,2}).*";
        String mobile = "&*（&*（dfda432";
        Pattern p = Pattern.compile(regex);
        Matcher m = p.matcher(mobile);
        boolean isMatch = m.matches();
        if (isMatch) {
            String group = m.group(1);
            System.out.println(group);
        } else {
            System.out.println("mismatch");
        }
    }

